mapper.select:
SELECT u.id id, u. userCode userCode, u.userName userName, u.birthday birthday, u.gender gender,u.phone phone, r.roleName userRoleName FROM smbms_user u JOIN smbms_role r ON u.userRole=r.id
and u.userName like "%"#{queryName}"%"
and u.userRole=#{roleId}
limit #{startIndex},#{pageSize}
